package digests

import (
	"encoding/json"
	"testing"

	"github.com/franela/goblin"
	"github.com/ion-channel/ionic/rulesets"
	"github.com/ion-channel/ionic/scanner"
	. "github.com/onsi/gomega"
)

func TestDigests(t *testing.T) {
	g := goblin.Goblin(t)
	RegisterFailHandler(func(m string, _ ...int) { g.Fail(m) })

	g.Describe("Digests", func() {
		g.Describe("New Digests", func() {
			g.It("should process community digests", func() {
				var analysisStatus scanner.AnalysisStatus
				var appliedRulesetSummary rulesets.AppliedRulesetSummary

				err := json.Unmarshal([]byte(scanSummary), &analysisStatus)
				Expect(err).To(BeNil())
				err = json.Unmarshal([]byte(appliedRuleset), &appliedRulesetSummary)
				Expect(err).To(BeNil())
				ds, err := NewDigests(&appliedRulesetSummary, analysisStatus.ScanStatus)

				Expect(ds).ToNot(BeNil())
				Expect(len(ds)).To(Equal(18))
				Expect(ds[16].RuleID).To(Equal("2981e1b0-0c8f-0137-8fe7-186590d3c755"))
				Expect(ds[17].RuleID).To(Equal("d3b66d48-40a1-11eb-b378-0242ac130002"))
			})

			g.It("should process all digests", func() {
				var analysisStatus scanner.AnalysisStatus
				var appliedRulesetSummary rulesets.AppliedRulesetSummary

				err := json.Unmarshal([]byte(mavenScanSummary), &analysisStatus)
				Expect(err).To(BeNil())
				err = json.Unmarshal([]byte(mavenAppliedRuleset), &appliedRulesetSummary)
				Expect(err).To(BeNil())

				ds, err := NewDigests(&appliedRulesetSummary, analysisStatus.ScanStatus)

				Expect(ds).ToNot(BeNil())
				Expect(len(ds)).To(Equal(18))
				Expect(ds[2].Index).To(Equal(CriticalVulnerabilitiesIndex))
				Expect([]byte(ds[2].SourceData)).ToNot(Equal([]byte(`{"type":"vulnerability","data":[]}`)))
				Expect(ds[8].Index).To(Equal(DirectDependencyIndex))
				Expect([]byte(ds[8].SourceData)).ToNot(Equal([]byte(`{"type":"dependency","data":[]}`)))
				Expect(ds[16].RuleID).To(Equal("2981e1b0-0c8f-0137-8fe7-186590d3c755"))
				Expect(ds[17].RuleID).To(Equal("d3b66d48-40a1-11eb-b378-0242ac130002"))
			})
		})
	})
}

